mapper.select:
SELECT h2.id, h3.create_time, h3.modify_time, h6.hf_province, h6.hf_city, h6.hf_conty, h6.is_fault_address, h6.hf_address_detail, h4.username, h3.purchase_quantity, h4.phone, h5.hf_name, h2.amount, h3.googs_id, h3.order_detail_status, h8.file_id, h7.hf_value, h4.nick_name, h1.user_address_id, h2.pay_method_name, h3.purchase_price, h1.user_id FROM hf_order_logistics h1 LEFT JOIN hf_orders h2 ON h1.orders_id = h2.id LEFT JOIN hf_orders_detail h3 ON h1.order_detail_id = h3.id LEFT JOIN hf_user h4 ON h1.user_id = h4.id LEFT JOIN hf_goods h5 ON h1.googs_id = h5.id LEFT JOIN hf_user_address h6 ON h6.user_id =h4.id LEFT JOIN hf_goods_pictrue h8 ON h8.goods_id = h5.id LEFT JOIN hf_goods_spec h7 ON h7.goods_id = h5.id WHERE 1=1
AND h2.id=#{orderId}
AND h5.hf_name like '%${hfName}%'
AND h2.pay_method_type=#{payMethodType}
AND h3.order_detail_status=#{orderDetailStatus}
AND h1.order_detail_id=#{orderDetailId}
AND h1.user_id=#{userId}
